mapper.select:
select *
from rwj_warehouse
where id = #{id}
  and is_deleted = 0;

mapper.update:
update rwj_warehouse
set is_deleted=0
where id = #{id}

update rwj_warehouse
set is_deleted=0
where warehouse_code = #{warehouseCode}

update rwj_warehouse
set warehouse_code=#{warehouseCode},
    warehouse_name=#{warehouseName},
    warehouse_stock=#{warehouseStock}
where id = #{id}
